<?php/** * Base Controller class */class Controller{    public $view;    public $model;    public function __construct(){        $this->view = new View();    }    /**     * Load Modules      */    public function loadModel($name){        $model   = "models/".$name."_model.php";        if(file_exists($model)){            require($model);            $modelName     = $name."_Model";            $this->model    = new $modelName();        }    }}