<?phpclass Router{    function __construct(){        $url    = isset($_GET["url"]) ? $_GET["url"] : null;        $url    = rtrim($url,'/');        $url    = explode('/' ,$url);        if(empty($url[0])){            require('controller/home.php');            $controller     = new Home();            $controller->loadModel("home");            $controller->index();            return false;        }        $file = 'controller/'.$url[0].'.php';        if(file_exists($file)){            require($file);        }else{            $this->error();        }        $controller     = new $url[0];        $controller->loadModel($url[0]);        if(isset($url[2])){            $_method_name   = 'execute'.$url[1];            if(method_exists($controller,$_method_name)){                $controller->{'execute'.$url[1]}($url[2]);            }else{                $this->error();            }        }else{            if(isset($url[1])){                $_method_name   = 'execute'.$url[1];                if(method_exists($controller,$_method_name)){                    $controller->{'execute'.$url[1]}();                }else{                    $this->error();                }            }else{                $controller->index();            }        }    }    function error(){         require("controller/error.php");         $controller     = new Error();         $controller->index();         return false;    }}